mapper.insert:
INSERT INTO REACT_MEMBER(
    MEM_ID
        , MEM_NAME
        , MEM_PW
        , MEM_TELL
        , MEM_INTRO
)
VALUES(
    #{memId}
    , #{memName}
    , #{memPw}
    , #{memTell}
    , #{memIntro}
)

mapper.update:
UPDATE REACT_MEMBER
SET
MEM_NAME = #{memName}
, MEM_PW = #{memPw}
, MEM_TELL = #{memTell}
, MEM_INTRO = #{memIntro}
WHERE MEM_ID = #{memId}
